/// count : 10
/// total : 0
/// nextPageUrl : "http://baobab.kaiyanapp.com/api/v3/specialTopics?start=10&num=10"
/// adExist : false

class TopicEntity extends PagingEntity<TopicItem>{

  int count;
  int total;

  bool adExist;

  TopicEntity({

      this.count, 
      this.total, 

      this.adExist});

  TopicEntity.fromJson(dynamic json) {
    if (json["itemList"] != null) {
      itemList = [];
      json["itemList"].forEach((v) {
        itemList.add(TopicItem.fromJson(v));
      });
    }
    count = json["count"];
    total = json["total"];
    nextPageUrl = json["nextPageUrl"];
    adExist = json["adExist"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    if (itemList != null) {
      map["itemList"] = itemList.map((v) => v.toJson()).toList();
    }
    map["count"] = count;
    map["total"] = total;
    map["nextPageUrl"] = nextPageUrl;
    map["adExist"] = adExist;
    return map;
  }

}

